(function(){function e(){const e=document.querySelectorAll(".highlight");e.forEach(e=>{if(e.querySelector(".copy-code-button"))return;const t=document.createElement("button");t.className="copy-code-button",t.type="button",t.innerText="Copy",t.addEventListener("click",()=>{const n=e.querySelectorAll("code"),s=n[n.length-1],o=s.innerText;navigator.clipboard.writeText(o).then(()=>{t.innerText="Copied!",t.classList.add("copied"),setTimeout(()=>{t.innerText="Copy",t.classList.remove("copied")},2e3)}).catch(e=>{console.error("Failed to copy code: ",e),t.innerText="Error"})});const n=e.querySelector("code[data-lang]");if(n){const s=n.getAttribute("data-lang").toUpperCase(),t=document.createElement("span");t.className="code-lang-label",t.innerText=s,e.appendChild(t)}e.appendChild(t)})}document.readyState==="loading"?document.addEventListener("DOMContentLoaded",e):e()})()