(function(){function e(){const e=document.querySelectorAll(".highlight");e.forEach(e=>{if(e.querySelector(".copy-code-button"))return;const t=document.createElement("button");t.className="copy-code-button",t.type="button",t.innerText="Copy",t.addEventListener("click",()=>{const n=e.querySelector("code").innerText;navigator.clipboard.writeText(n).then(()=>{t.innerText="Copied!",t.classList.add("copied"),setTimeout(()=>{t.innerText="Copy",t.classList.remove("copied")},2e3)}).catch(e=>{console.error("Failed to copy code: ",e),t.innerText="Error"})}),e.appendChild(t)})}document.readyState==="loading"?document.addEventListener("DOMContentLoaded",e):e()})()